//新建一个Vue实例作为中央事件总线新建在Vue的prototype上方便全局使用
	Vue.prototype.bus = new Vue();
	Vue.component('child',{
		props: {
			content: String
		},
		data(){
			return {
				str = this.content
			}
		},
		template: '<div @click="handleClick">{{str}}</div>',
		methods: {
